head -40 /var/www/html/config.json
{
  "database": {
    "host": "localhost",
    "user": "admin",
    "password": "adminpass",
    "name": "production_db"
  },
  "logging": {
    "level": "info",
    "file": "/var/log/server.log"
  },
  "features": {
    "enable_backups": true,
    "max_upload_size": 10485760
  }
}
root@ubuntu:~$

head -40 /var/www/html/db_settings.json
{
  "backup": {
    "enabled": true,
    "schedule": "0 2 * * *",
    "destination": "/var/backups"
  },
  "database": {
    "host": "localhost",
    "username": "backup_user",
    "password": "backuppass"
  },
  "logging": {
    "level": "warning",
    "file": "/var/log/backup.log"
  }
}
root@ubuntu:~$
