#!/bin/bash
# Backup script for important files

tar -czf /var/backups/home_user_$(date +%F).tar.gz /home/user

# Rotate backups older than 7 days
find /var/backups -type f -name "home_user_*.tar.gz" -mtime +7 -exec rm {} \;

[Notice] SIGTERM received mid-script, some backup tasks may be incomplete.
