/* chat_server.c - Simple TCP chat server
 *
 * Created: Nov 1, 2001
 * Author: user
 *
 * This server accepts multiple TCP connections from clients,
 * and broadcasts any message received to all connected clients.
 */

#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>
#include <sys/types.h>
#include <sys/socket.h>
#include <netinet/in.h>
#include <arpa/inet.h>
#include <errno.h>

#define PORT 9000
#define MAX_CLIENTS 10
#define BUFFER_SIZE 1024

int main() {
    int server_fd, client_fds[MAX_CLIENTS];
    struct sockaddr_in server_addr, client_addr;
    fd_set read_fds;
    char buffer[BUFFER_SIZE];
    int max_fd, i, activity, addr_len;

    // Initialize client file descriptors to 0 (unused)
    for(i = 0; i < MAX_CLIENTS; i++) 
        client_fds[i] = 0;

    // Create server socket
    if((server_fd = socket(AF_INET, SOCK_STREAM, 0)) == 0) {
        perror("socket failed");
        exit(EXIT_FAILURE);
    }

    // Setup server address struct
    server_addr.sin_family = AF_INET;
    server_addr.sin_addr.s_addr = INADDR_ANY;
    server_addr.sin_port = htons(PORT);

    // Bind socket to port
    if (bind(server_fd, (struct sockaddr *)&server_addr, sizeof(server_addr)) < 0) {
        perror("bind failed");
        exit(EXIT_FAILURE);
    }

    // Listen for incoming connections
    if (listen(server_fd, 3) < 0) {
        perror("listen");
        exit(EXIT_FAILURE);
    }

    printf("Chat server started on port %d\n", PORT);
    addr_len = sizeof(client_addr);

    while(1) {
        FD_ZERO(&read_fds);
        FD_SET(server_fd, &read_fds);
        max_fd = server_fd;

        // Add client sockets to set
        for(i = 0; i < MAX_CLIENTS; i++) {
            if(client_fds[i] > 0) {
                FD_SET(client_fds[i], &read_fds);
                if(client_fds[i] > max_fd)
                    max_fd = client_fds[i];
            }
        }

        // Wait for activity
        activity = select(max_fd + 1, &read_fds, NULL, NULL, NULL);

        if ((activity < 0) && (errno != EINTR)) {
            perror("select error");
        }

        // Incoming connection
        if (FD_ISSET(server_fd, &read_fds)) {
            int new_socket = accept(server_fd, (struct sockaddr *)&client_addr, (socklen_t*)&addr_len);
            if (new_socket < 0) {
                perror("accept");
                exit(EXIT_FAILURE);
            }

            // Add new socket to client_fds
            for(i = 0; i < MAX_CLIENTS; i++) {
                if(client_fds[i] == 0) {
                    client_fds[i] = new_socket;
                    break;
                }
            }

            printf("New connection: socket fd %d, ip %s, port %d\n", new_socket,
                inet_ntoa(client_addr.sin_addr), ntohs(client_addr.sin_port));
        }

        // IO operation on client sockets
        for(i = 0; i < MAX_CLIENTS; i++) {
            int sd = client_fds[i];
            if (FD_ISSET(sd, &read_fds)) {
                int valread = read(sd, buffer, BUFFER_SIZE);
                if (valread == 0) {
                    // Client disconnected
                    getpeername(sd, (struct sockaddr*)&client_addr, (socklen_t*)&addr_len);
                    printf("Client disconnected: ip %s, port %d\n",
                        inet_ntoa(client_addr.sin_addr), ntohs(client_addr.sin_port));
                    close(sd);
                    client_fds[i] = 0;
                } else {
                    // Broadcast message to other clients
                    buffer[valread] = '\0';
                    for(int j = 0; j < MAX_CLIENTS; j++) {
                        if(client_fds[j] != 0 && client_fds[j] != sd) {
                            send(client_fds[j], buffer, valread, 0);
                        }
                    }
                }
            }
        }
    }

    return 0;
}
