from flask import Flask, request, jsonify

app = Flask(__name__)

# Simple secret validation endpoint
@app.route('/secret', methods=['POST'])
def secret():
    data = request.json
    key = data.get('key', '')
    if key == "abc123":
        return jsonify({"status": "success", "message": "Access granted to the secret data."})
    else:
        return jsonify({"status": "failure", "message": "Invalid key."}), 403

if __name__ == '__main__':
    app.run(host='0.0.0.0', port=8000)

