#!/bin/sh

# /etc/cron.daily/passwd: Ensure permissions and integrity of password files

chmod 0644 /etc/passwd
chmod 0640 /etc/shadow /etc/gshadow
chown root:root /etc/passwd /etc/shadow /etc/gshadow /etc/group

# Check for any extra backup or tmp files
find /etc -name 'passwd-*' -exec rm -f {} \;
find /etc -name 'shadow-*' -exec rm -f {} \;
root@ubuntu:~$

