import os
import time
import shutil

BACKUP_SOURCE = "/home/user/Documents/"
BACKUP_DEST = "/srv/backup/user_documents/"
LOG_FILE = "/var/log/backup.log"

def log(message):
    with open(LOG_FILE, "a") as logf:
        logf.write(f"{time.strftime('%Y-%m-%d %H:%M:%S')} {message}\n")

def perform_backup():
    if not os.path.exists(BACKUP_DEST):
        os.makedirs(BACKUP_DEST)
    try:
        for filename in os.listdir(BACKUP_SOURCE):
            src_path = os.path.join(BACKUP_SOURCE, filename)
            dst_path = os.path.join(BACKUP_DEST, filename)
            if os.path.isfile(src_path):
                shutil.copy2(src_path, dst_path)
                log(f"Copied {src_path} to {dst_path}")
