#!/bin/sh
# Cleanup script to remove old temporary files and logs

find /tmp -type f -atime +7 -exec rm -f {} \;
find /var/log -name "*.log" -type f -mtime +30 -exec gzip {} \;
find /var/log -name "*.log.gz" -type f -mtime +90 -exec rm -f {} \;

# Rotate system logs
/usr/sbin/logrotate /etc/logrotate.conf

exit 0
