#!/bin/sh
# Run system activity accounting.

# Configuration file
SYSSTAT_CONF="/etc/sysstat/sysstat"

if [ -r "$SYSSTAT_CONF" ]; then
    . "$SYSSTAT_CONF"
fi

PATH=${PATH:-:/usr/bin:/bin}

_sadir=${SADIR:-"/var/log/sa"}
_sadc=${SADC:-"/usr/lib/sysstat/sadc"}

interval=${interval:-10}
samples=${samples:-1}

if [ -x "$_sadc" ]; then
    $_sadc -S DISK -F -s "$(date +%H:%M:%S)" -S XDISK -S INT -S FLOP -S MSG -S SWAP -S EOF -S NSR -S NFR -S SIG -S CCH -S CSW -S SCS -S SYS -S CPU -S BOND -S INTR -S SOFT -S MSG -S PAGE -S VM -S ROC -S RFS -S TSF -S KS -S NPI -S SOFT -S US -S GS -S VMSTAT -S DEVSTAT -S NFSD -S BLK -S VZNET -S ARC -S SLA -S VS -S RDMA -S GPU "${interval}" "${samples}" "${_sadir}/sa$(date +%d)"
fi

# End of script

