#!/bin/bash
# Custom script for system maintenance

echo "Starting custom maintenance tasks..."

# Rotate logs older than 7 days
find /var/log -type f -mtime +7 -exec gzip {} \;

# Clean temporary files older than 3 days
find /tmp -type f -mtime +3 -exec rm -f {} \;

echo "Maintenance tasks completed."

# End of script
